// src/hooks/wallet/useReportTransactionEffects.ts
import { toBase64 as toBase642 } from "@mysten/sui/utils";
import { useMutation as useMutation5 } from "@tanstack/react-query";
function useReportTransactionEffects({
  mutationKey,
  ...mutationOptions
} = {}) {
  const {
    currentWallet
  } = useCurrentWallet();
  const currentAccount = useCurrentAccount();
  return useMutation5({
    mutationKey: walletMutationKeys.reportTransactionEffects(mutationKey),
    mutationFn: async ({
      effects,
      chain = currentWallet?.chains[0],
      account = currentAccount
    }) => {
      if (!currentWallet) {
        throw new WalletNotConnectedError("No wallet is connected.");
      }
      if (!account) {
        throw new WalletNoAccountSelectedError("No wallet account is selected to report transaction effects for");
      }
      const reportTransactionEffectsFeature = currentWallet.features["sui:reportTransactionEffects"];
      if (reportTransactionEffectsFeature) {
        return await reportTransactionEffectsFeature.reportTransactionEffects({
          effects: Array.isArray(effects) ? toBase642(new Uint8Array(effects)) : effects,
          account,
          chain: chain ?? currentWallet?.chains[0]
        });
      }
    },
    ...mutationOptions
  });
}

// src/hooks/wallet/useSignAndExecuteTransaction.ts
function useSignAndExecuteTransaction({
  mutationKey,
  execute,
  ...mutationOptions
} = {}) {
  const {
    currentWallet,
    supportedIntents
  } = useCurrentWallet();
  const currentAccount = useCurrentAccount();
  const client = useSuiClient();
  const {
    mutate: reportTransactionEffects
  } = useReportTransactionEffects();
  const executeTransaction = execute ?? (async ({
    bytes,
    signature
  }) => {
    const {
      digest,
      rawEffects
    } = await client.executeTransactionBlock({
      transactionBlock: bytes,
      signature,
      options: {
        showRawEffects: true
      }
    });
    return {
      digest,
      rawEffects,
      effects: toBase643(new Uint8Array(rawEffects)),
      bytes,
      signature
    };
  });
  return useMutation6({
    mutationKey: walletMutationKeys.signAndExecuteTransaction(mutationKey),
    mutationFn: async ({
      transaction,
      ...signTransactionArgs
    }) => {
      if (!currentWallet) {
        throw new WalletNotConnectedError("No wallet is connected.");
      }
      const signerAccount = signTransactionArgs.account ?? currentAccount;
      if (!signerAccount) {
        throw new WalletNoAccountSelectedError("No wallet account is selected to sign the transaction with.");
      }
      const chain = signTransactionArgs.chain ?? signerAccount?.chains[0];
      if (!currentWallet.features["sui:signTransaction"] && !currentWallet.features["sui:signTransactionBlock"]) {
        throw new WalletFeatureNotSupportedError("This wallet doesn't support the `signTransaction` feature.");
      }
      const {
        signature,
        bytes
      } = await signTransaction(currentWallet, {
        ...signTransactionArgs,
        transaction: {
          async toJSON() {
            return typeof transaction === "string" ? transaction : await transaction.toJSON({
              supportedIntents,
              client
            });
          }
        },
        account: signerAccount,
        chain: signTransactionArgs.chain ?? signerAccount.chains[0]
      });
      const result = await executeTransaction({
        bytes,
        signature
      });
      let effects;
      if ("effects" in result && result.effects?.bcs) {
        effects = result.effects.bcs;
      } else if ("rawEffects" in result) {
        effects = toBase643(new Uint8Array(result.rawEffects));
      } else {
        throw new Error("Could not parse effects from transaction result.");
      }
      reportTransactionEffects({
        effects,
        account: signerAccount,
        chain
      });
      return result;
    },
    ...mutationOptions
  });
}

// src/hooks/wallet/useSignPersonalMessage.ts
import { useMutation as useMutation7 } from "@tanstack/react-query";
function useSignPersonalMessage({
  mutationKey,
  ...mutationOptions
} = {}) {
  const {
    currentWallet
  } = useCurrentWallet();
  const currentAccount = useCurrentAccount();
  return useMutation7({
    mutationKey: walletMutationKeys.signPersonalMessage(mutationKey),
    mutationFn: async signPersonalMessageArgs => {
      if (!currentWallet) {
        throw new WalletNotConnectedError("No wallet is connected.");
      }
      const signerAccount = signPersonalMessageArgs.account ?? currentAccount;
      if (!signerAccount) {
        throw new WalletNoAccountSelectedError("No wallet account is selected to sign the personal message with.");
      }
      const signPersonalMessageFeature = currentWallet.features["sui:signPersonalMessage"];
      if (signPersonalMessageFeature) {
        return await signPersonalMessageFeature.signPersonalMessage({
          ...signPersonalMessageArgs,
          account: signerAccount
        });
      }
      const signMessageFeature = currentWallet.features["sui:signMessage"];
      if (signMessageFeature) {
        console.warn("This wallet doesn't support the `signPersonalMessage` feature... falling back to `signMessage`.");
        const {
          messageBytes,
          signature
        } = await signMessageFeature.signMessage({
          ...signPersonalMessageArgs,
          account: signerAccount
        });
        return {
          bytes: messageBytes,
          signature
        };
      }
      throw new WalletFeatureNotSupportedError("This wallet doesn't support the `signPersonalMessage` feature.");
    },
    ...mutationOptions
  });
}

// src/hooks/wallet/useSignTransaction.ts
import { signTransaction as signTransaction2 } from "@mysten/wallet-standard";
import { useMutation as useMutation8 } from "@tanstack/react-query";
function useSignTransaction({
  mutationKey,
  ...mutationOptions
} = {}) {
  const {
    currentWallet
  } = useCurrentWallet();
  const currentAccount = useCurrentAccount();
  const client = useSuiClient();
  const {
    mutate: reportTransactionEffects
  } = useReportTransactionEffects();
  return useMutation8({
    mutationKey: walletMutationKeys.signTransaction(mutationKey),
    mutationFn: async ({
      transaction,
      ...signTransactionArgs
    }) => {
      if (!currentWallet) {
        throw new WalletNotConnectedError("No wallet is connected.");
      }
      const signerAccount = signTransactionArgs.account ?? currentAccount;
      if (!signerAccount) {
        throw new WalletNoAccountSelectedError("No wallet account is selected to sign the transaction with.");
      }
      if (!currentWallet.features["sui:signTransaction"] && !currentWallet.features["sui:signTransactionBlock"]) {
        throw new WalletFeatureNotSupportedError("This wallet doesn't support the `signTransaction` feature.");
      }
      const {
        bytes,
        signature
      } = await signTransaction2(currentWallet, {
        ...signTransactionArgs,
        transaction: {
          toJSON: async () => {
            return typeof transaction === "string" ? transaction : await transaction.toJSON({
              supportedIntents: [],
              client
            });
          }
        },
        account: signerAccount,
        chain: signTransactionArgs.chain ?? signerAccount.chains[0]
      });
      return {
        bytes,
        signature,
        reportTransactionEffects: effects => {
          reportTransactionEffects({
            effects,
            account: signerAccount,
            chain: signTransactionArgs.chain ?? signerAccount.chains[0]
          });
        }
      };
    },
    ...mutationOptions
  });
}
export { ConnectButton, ConnectModal, SuiClientContext, SuiClientProvider, WalletProvider, createNetworkConfig, getSuiClientQuery, lightTheme, useAccounts, useAutoConnectWallet, useConnectWallet, useCurrentAccount, useCurrentWallet, useDisconnectWallet, useReportTransactionEffects, useResolveSuiNSName, useSignAndExecuteTransaction, useSignPersonalMessage, useSignTransaction, useSuiClient, useSuiClientContext, useSuiClientInfiniteQuery, useSuiClientMutation, useSuiClientQueries, useSuiClientQuery, useSuiClientSuspenseQuery, useSwitchAccount, useWallets };

